package com.enovka.gemini4j.model;

import com.fasterxml.jackson.annotation.JsonProperty;
import lombok.AllArgsConstructor;
import lombok.Builder;
import lombok.Data;
import lombok.NoArgsConstructor;
import lombok.experimental.Accessors;

/**
 * Attribution for a source that contributed to an answer. This class
 * provides information about the source that contributed to a grounded answer
 * generated by the Gemini API.
 *
 * <p>It includes the identifier of the source and the content that makes up
 * the attribution.
 *
 * @author Everson Novka &lt;enovka@gmail.com&gt;
 * @since 0.0.2
 */
@Data
@Accessors(chain = true)
@Builder(setterPrefix = "with", toBuilder = true)
@NoArgsConstructor
@AllArgsConstructor
public class GroundingAttribution {

    /**
     * Output only. Identifier for the source contributing to this attribution.
     */
    @JsonProperty("sourceId")
    private AttributionSourceId sourceId;

    /**
     * Grounding source content that makes up this attribution.
     */
    @JsonProperty("content")
    private Content content;
}